// Parse the XML data and extract the necessary information
$xml = simplexml_load_file('magical_creatures.xml');
foreach ($xml-creature as $creature) {
 $name = $creature->name;
 $type = $creature->type;
 $powers = $creature->powers;
 $attributes = $creature->attributes;
 // Generate a unique ID for the creature
 $id = uniqid();
 // Insert the creature's data into the SQL database table
 $pdo = new PDO('mysql:host=localhost;dbname=mydatabase','username','password');
 $stmt = $pdo->prepare('INSERT INTO creatures (id,name,type,

powers,attributes) VALUES (?)');
 $stmt->execute([$id,$name,$type,$powers,$attributes]);
}?>
